SELECT a.* FROM (
  SELECT @RN:=@RN+1 AS rownum, b.* FROM(
    SELECT * FROM camping WHERE addr LIKE CONCAT(#{addr}, '%') AND category  LIKE CONCAT('%', #{category}, '%')) b,
    (SELECT @RN:=0) r
  ) a
WHERE rownum BETWEEN #{begin} AND #{end}

