mapper.select:
Select COUNT(*) from iamcamper.like
    Where m_idx = #{m_idx}
    And b_idx = #{b_idx}

Select COUNT(*) from iamcamper.like
    Where m_idx = #{m_idx}
    And c_idx = #{c_idx}

mapper.update:
UPDATE iamcamper.bbs b JOIN iamcamper.like l ON b.b_idx = l.b_idx
SET b.like = b.like + 1 , l.status = 1
WHERE l.b_idx = #{b_idx} AND b.b_idx = #{b_idx} AND l.m_idx = #{m_idx}

UPDATE iamcamper.bbs b JOIN iamcamper.like l ON b.b_idx = l.b_idx
SET b.like = b.like -1 , l.status = 0
WHERE l.b_idx = #{b_idx} AND b.b_idx = #{b_idx} AND l.m_idx = #{m_idx}

UPDATE iamcamper.comment c JOIN iamcamper.like l ON c.c_idx = l.c_idx
SET c.like = c.like + 1 , l.status = 1
WHERE l.c_idx = #{c_idx} AND c.c_idx = #{c_idx} AND l.m_idx = #{m_idx}

UPDATE iamcamper.comment c JOIN iamcamper.like l ON c.c_idx = l.c_idx
SET c.like = c.like - 1 , l.status = 0
WHERE l.c_idx = #{c_idx} AND c.c_idx = #{c_idx} AND l.m_idx = #{m_idx}
